function openFullscreen() {
  if (marketingVideo.requestFullscreen) {
    marketingVideo.requestFullscreen();
  } else if (marketingVideo.mozRequestFullScreen) { /* Firefox */
    marketingVideo.mozRequestFullScreen();
  } else if (marketingVideo.webkitRequestFullscreen) { /* Chrome, Safari & Opera */
    marketingVideo.webkitRequestFullscreen();
  } else if (marketingVideo.msRequestFullscreen) { /* IE/Edge */
    marketingVideo.msRequestFullscreen();
  } else {
    // Replace to Youtube video link when available
